mapper.select:
select * from user_info 
where user_id in (
	select user_id from products_info where product_id in (
		select pboard.product_id from (
			select board.*, row_number() over(partition by pboard_unit_condition, product_id order by pboard_unit_updatedate desc, pboard_unit_regdate asc) each_rank from product_board board
		) pboard where each_rank='1'
	)
)

mapper.update:
update user_info
user_email=#{user_email}, user_name=#{user_name}, user_password=#{user_password}, user_contact=#{user_contact}, user_enabled=#{user_enabled}, user_type=#{user_type}, user_gender=#{user_gender}, user_birth=to_date(#{user_birth},'YYYY-MM-DD hh24:mi:ss'), user_address=#{user_address}, user_interesting=#{user_interesting}, user_enabledContent=#{user_enabledContent}, file_pictureId=#{file_pictureId}
where user_id= #{user_id}
